    @Override
    public void destroy() {
        Iterator iter = mComponentMap.entrySet().iterator();
        while (iter.hasNext()) {
            Map.Entry entry = (Map.Entry) iter.next();
            WXGCanvasWeexComponent val = (WXGCanvasWeexComponent) entry.getValue();
            GLog.d("component destroy id=" + entry.getKey());
        }

        mComponentMap.clear();
    }

