    @JSMethod(uiThread = false)
    public void getImageData(String rectArray, String refid, JSCallback callback) {
        String getImageData;
        try {
            JSONArray param = new JSONArray(rectArray);
            int x = param.optInt(0), y = param.optInt(1), width = param.optInt(2), height = param.optInt(3);
            getImageData = "R" + x + "," + y + "," + width + "," + height + ";";
            String imagedata = mImpl.render(refid, getImageData, true);
//            Log.e("返回的数据",imagedata);
            if (callback != null) {
                Map<String, Object> values = new HashMap<>();
                values.put("data", imagedata);
                values.put("height", height);
                values.put("width", width);
                callback.invoke(values);
            }
        } catch (JSONException e) {
            if (callback != null) {
                Map<String, Object> values = new HashMap<>();
                values.put("data", "");
                values.put("height", 0);
                values.put("width", 0);
                callback.invoke(values);
            }
        }

    }


    @JSMethod(uiThread = false)
    public void putImageData(String rectArray, String componentId, JSCallback callback) {
        try {
            JSONArray param = new JSONArray(rectArray);
            int x = param.optInt(0), y = param.optInt(1), width = param.optInt(2), height = param.optInt(3);
            String imageData = param.optString(4);
            String putImageData = "P" + x + "," + y + "," + width + "," + height + "," + width + "," + height + "," + imageData + ";";
            mImpl.render(componentId, putImageData, false);
            if (callback != null) {
                callback.invoke(new HashMap<>());
            }
        } catch (JSONException e) {
            if (callback != null) {
                callback.invoke(new HashMap<>());
            }
        }

    }

    @JSMethod(uiThread = false)
    public com.alibaba.fastjson.JSONObject measureText(String textStyle, String componentId) {
        com.alibaba.fastjson.JSONObject fjob = new com.alibaba.fastjson.JSONObject();
        try {
            JSONArray param = new JSONArray(textStyle);
            String text = param.optString(0);
            String commondStr = "K" + text + ";";
            String width = mImpl.render(componentId, commondStr, true);
            width = String.format(Locale.US, "%.2f", Float.parseFloat(width));
            fjob.put("width", width);
            return fjob;
        } catch (JSONException e) {
            fjob.put("width", "-1");
            return fjob;
        }
    }

    @JSMethod(uiThread = false)
    public void toTempFilePath(String params, String refid, JSCallback callback) {
        try {
            JSONArray param = new JSONArray(params);
            int x = param.optInt(0), y = param.optInt(1), width = param.optInt(2), height = param.optInt(3), destwidth = param.optInt(4), destheight = param.optInt(5);
            String filetype = param.optString(6);
            float quality = param.optInt(7);
            String gimageData = "R" + x + "," + y + "," + width + "," + height + ";";
            String data = mImpl.render(refid, gimageData, true);
            byte[] imageData = GCanvasBase64.decode(data.getBytes());
            int[] colorInt = btoi(imageData);
            Bitmap bitmap = null;
            if (colorInt != null)
                bitmap = Bitmap.createBitmap(colorInt, destwidth, destheight, Bitmap.Config.ARGB_8888);
            Bitmap.CompressFormat format = filetype.equals("jpg") ? Bitmap.CompressFormat.JPEG : Bitmap.CompressFormat.PNG;
            quality = quality * 100;
            HashMap<String, Object> values = new HashMap<>();
            if (quality > 100 || quality < 0) {
                values.put("type", "fail");
                if (callback != null) callback.invoke(values);
                return;
            }
            if (bitmap != null) {
                try {
                    String path = "_doc/" + System.currentTimeMillis() + "." + (filetype.equals("jpg") ? "jpg" : "png");
                    Uri pathUri = mWXSDKInstance.rewriteUri(Uri.parse(path), URIAdapter.IMAGE);
                    FileOutputStream fos = new FileOutputStream(new File(pathUri.getPath()));
                    bitmap.compress(format, (int) quality, fos);
                    fos.flush();
                    fos.close();
                    values.put("type", "success");
                    values.put("tempFilePath", pathUri.getPath());
                    if (callback != null) callback.invoke(values);
                } catch (IOException e) {
                    values.put("type", "fail");
                    if (callback != null) callback.invoke(values);
                }
            } else {
                values.put("type", "fail");
                if (callback != null) callback.invoke(values);
            }
        } catch (JSONException e) {
            HashMap<String, Object> values = new HashMap<>();
            values.put("type", "fail");
            if (callback != null) callback.invoke(values);
        }

    }

    // byte[] to int[]
    private int[] btoi(byte[] btarr) {
        if (btarr.length % 4 != 0) {
            return null;
        }
        int[] intarr = new int[btarr.length / 4];

        int i1, i2, i3, i4;
        for (int j = 0, k = 0; j < intarr.length; j++, k += 4)//j循环int		k循环byte数组
        {
            i1 = btarr[k];
            i2 = btarr[k + 1];
            i3 = btarr[k + 2];
            i4 = btarr[k + 3];

            if (i1 < 0) {// R
                i1 += 256;
            }
            if (i2 < 0) {// G
                i2 += 256;
            }
            if (i3 < 0) {// B
                i3 += 256;
            }
            if (i4 < 0) {// A
                i4 += 256;
            }
//            (A & 0xff) << 24 | (B & 0xff) << 16 | (G & 0xff) << 8 | (R & 0xff);
//            intarr[j] = ((i4 & 0xff) << 24) + ((i3 & 0xff) << 16) + ((i2 & 0xff) << 8) + (i1 & 0xff);//保存Int数据类型转换
            intarr[j] = (i4 & 0xff) << 24 | (i1 & 0xff) << 16 | (i2 & 0xff) << 8 | (i3 & 0xff);// ARGB

        }
        return intarr;
    }
